import React, { useState } from 'react';
import axios from 'axios';
import { Button, Typography, Box, Table, TableBody, TableCell, TableContainer, TableHead, TableRow, Paper, Snackbar, Alert, Dialog, DialogTitle, DialogContent, DialogActions, TextField, IconButton } from '@mui/material';
import EditIcon from '@mui/icons-material/Edit';
import DeleteIcon from '@mui/icons-material/Delete';
  const [editOpen, setEditOpen] = useState(false);
  const [editStudent, setEditStudent] = useState(null);

  const handleEditOpen = (student) => {
    setEditStudent({ ...student });
    setEditOpen(true);
  };

  const handleEditClose = () => {
    setEditOpen(false);
    setEditStudent(null);
  };

  const handleEditChange = (e) => {
    setEditStudent({ ...editStudent, [e.target.name]: e.target.value });
  };

  const handleEditSave = async () => {
    try {
      await axios.put(`http://localhost:5000/students/${editStudent._id}`, {
        student_id: editStudent.student_id,
        student_name: editStudent.student_name,
        total_marks: editStudent.total_marks,
        marks_obtained: editStudent.marks_obtained,
      });
      setMessage('Student updated');
      setError('');
      setOpen(true);
      setEditOpen(false);
      fetchStudents();
    } catch (err) {
      setError('Update failed');
      setMessage('');
      setOpen(true);
    }
  };

  const handleDelete = async (id) => {
    if (!window.confirm('Delete this student?')) return;
    try {
      await axios.delete(`http://localhost:5000/students/${id}`);
      setMessage('Student deleted');
      setError('');
      setOpen(true);
      fetchStudents();
    } catch (err) {
      setError('Delete failed');
      setMessage('');
      setOpen(true);
    }
  };

function App() {
  const [file, setFile] = useState(null);
  const [students, setStudents] = useState([]);
  const [total, setTotal] = useState(0);
  const [message, setMessage] = useState('');
  const [error, setError] = useState('');
  const [open, setOpen] = useState(false);

  const handleFileChange = (e) => {
    setFile(e.target.files[0]);
  };

  const handleUpload = async () => {
    if (!file) return;
    const formData = new FormData();
    formData.append('file', file);
    try {
      const res = await axios.post('http://localhost:5000/upload', formData, {
        headers: { 'Content-Type': 'multipart/form-data' },
      });
      setMessage(res.data.message + ` (${res.data.count} records)`);
      setError('');
      setOpen(true);
      fetchStudents();
    } catch (err) {
      setError(err.response?.data?.error || 'Upload failed');
      setMessage('');
      setOpen(true);
    }
  };

  const fetchStudents = async () => {
    try {
      const res = await axios.get('http://localhost:5000/students');
      setStudents(res.data.students);
      setTotal(res.data.total);
    } catch {
      setStudents([]);
      setTotal(0);
    }
  };

  React.useEffect(() => {
    fetchStudents();
  }, []);

  return (
    <Box sx={{ p: 4 }}>
      <Typography variant="h4" gutterBottom>Student Grade Management</Typography>
      <Box sx={{ mb: 2 }}>
        <input type="file" accept=".xlsx,.csv" onChange={handleFileChange} />
        <Button variant="contained" onClick={handleUpload} sx={{ ml: 2 }}>Upload</Button>
      </Box>
      <Typography variant="subtitle1">Total Students: {total}</Typography>
      <TableContainer component={Paper} sx={{ mt: 2 }}>
        <Table>
          <TableHead>
            <TableRow>
              <TableCell>Student ID</TableCell>
              <TableCell>Name</TableCell>
              <TableCell>Total Marks</TableCell>
              <TableCell>Marks Obtained</TableCell>
              <TableCell>Percentage</TableCell>
              <TableCell>Actions</TableCell>
            </TableRow>
          </TableHead>
          <TableBody>
            {students.map((s) => (
              <TableRow key={s._id}>
                <TableCell>{s.student_id}</TableCell>
                <TableCell>{s.student_name}</TableCell>
                <TableCell>{s.total_marks}</TableCell>
                <TableCell>{s.marks_obtained}</TableCell>
                <TableCell>{s.percentage.toFixed(2)}%</TableCell>
                <TableCell>
                  <IconButton color="primary" onClick={() => handleEditOpen(s)}><EditIcon /></IconButton>
                  <IconButton color="error" onClick={() => handleDelete(s._id)}><DeleteIcon /></IconButton>
                </TableCell>
              </TableRow>
            ))}
      <Dialog open={editOpen} onClose={handleEditClose}>
        <DialogTitle>Edit Student</DialogTitle>
        <DialogContent sx={{ display: 'flex', flexDirection: 'column', gap: 2, minWidth: 300 }}>
          <TextField label="Student ID" name="student_id" value={editStudent?.student_id || ''} onChange={handleEditChange} fullWidth />
          <TextField label="Name" name="student_name" value={editStudent?.student_name || ''} onChange={handleEditChange} fullWidth />
          <TextField label="Total Marks" name="total_marks" type="number" value={editStudent?.total_marks || ''} onChange={handleEditChange} fullWidth />
          <TextField label="Marks Obtained" name="marks_obtained" type="number" value={editStudent?.marks_obtained || ''} onChange={handleEditChange} fullWidth />
        </DialogContent>
        <DialogActions>
          <Button onClick={handleEditClose}>Cancel</Button>
          <Button onClick={handleEditSave} variant="contained">Save</Button>
        </DialogActions>
      </Dialog>
          </TableBody>
        </Table>
      </TableContainer>
      <Snackbar open={open} autoHideDuration={4000} onClose={() => setOpen(false)}>
        {message ? (
          <Alert onClose={() => setOpen(false)} severity="success">{message}</Alert>
        ) : error ? (
          <Alert onClose={() => setOpen(false)} severity="error">{error}</Alert>
        ) : null}
      </Snackbar>
    </Box>
  );
}

export default App;
